/***/
var Server = require('websockets/lib/websockets/server');
var Socket = require('websockets/lib/websockets/socket');

exports.Server = Server;
exports.WebSocket = Socket;

exports.createServer = (function() {
  function S(args) {
    return Server.apply(this, args);
  }
  S.prototype = Server.prototype;
  
  return function() {
    var args = arguments;
    return new S(args);
  };
})();
